  `type_id` int(11) default '0' COMMENT '类别',
  PRIMARY KEY  (`id`),
  KEY `blogType` (`type_id`),
  CONSTRAINT `blogType` FOREIGN KEY (`type_id`) REFERENCES `t_blogtype` (`id`) ON UPDATE CASCADE
